sudo dd if=/dev/zero of=/media/$(lsblk -o MOUNTPOINT,NAME | grep '/media' | awk '{print $1}')/persistent.img bs=1M count=2048 && sudo mkfs.ext4 /media/$(lsblk -o MOUNTPOINT,NAME | grep '/media' | awk '{print $1}')/persistent.img && sudo mkdir /mnt/persistent && sudo mount /media/$(lsblk -o MOUNTPOINT,NAME | grep '/media' | awk '{print $1}')/persistent.img /mnt/persistent && sudo sed -i "s/linux/linux persistent persistence=LABEL=$(lsblk -o MOUNTPOINT,LABEL | grep '/media' | awk '{print $2}')/" /boot/grub/grub.cfg
