sudo dd if=/dev/zero of=/media/$(df | grep '/dev/sd' | awk '{print $1}')/persistent.img bs=1M count=2048 && sudo mkfs.ext4 /media/$(df | grep '/dev/sd' | awk '{print $1}')/persistent.img && sudo mkdir /mnt/persistent && sudo mount /media/$(df | grep '/dev/sd' | awk '{print $1}')/persistent.img /mnt/persistent && sudo sed -i "s/linux/linux persistent persistence=LABEL=$(lsblk -o name,label | grep -E '/dev/sd[a-z]+' | awk '{print $2}')/" /boot/grub/grub.cfg
